<?php// Inclusion du fichier API OAuth Twitterrequire_once('twitteroauth/twitteroauth.php');// Définition des codes API Twitterdefine('CONSUMER_KEY', 'VOTRE_CONSUMER_KEY');define('CONSUMER_SECRET', 'VOTRE_CONSUMER_SECRET');define('ACCESS_TOKEN', 'VOTRE_ACCESS_KEY');define('ACCESS_TOKEN_SECRET', 'VOTRE_ACCESS_TOKEN_SECRET');// Connexion OAuth Twitter$connection = new TwitterOAuth(CONSUMER_KEY, CONSUMER_SECRET, ACCESS_TOKEN, ACCESS_TOKEN_SECRET);// Définition du message à envoyer, et envoie du message sur Twitterdate_default_timezone_set('GMT');$parameters = array('status' => 'Exemple de Tweet à envoyer sur #Twitter via @pirmax');$status = $connection->post('statuses/update', $parameters);?>