<?php// Inclusion du fichier API OAuth Twitterrequire_once('twitteroauth/twitteroauth.php');// Dfinition des codes API Twitterdefine('CONSUMER_KEY', 'VOTRE_CONSUMER_KEY');define('CONSUMER_SECRET', 'VOTRE_CONSUMER_SECRET');define('ACCESS_TOKEN', 'VOTRE_ACCESS_KEY');define('ACCESS_TOKEN_SECRET', 'VOTRE_ACCESS_TOKEN_SECRET');// Connexion OAuth Twitter$connection = new TwitterOAuth(CONSUMER_KEY, CONSUMER_SECRET, ACCESS_TOKEN, ACCESS_TOKEN_SECRET);// Dfinition des 5 messages  tweeter automatiquement$tweetMsg = array(	'@pirmax est l\'auteur de l\'article sur la cration d\'un robot Twitter.',	'Vous aussi, vous pouvez lire @LeBlogdeMaxence.',	'Lisez cet article http://www.maxence-blog.fr/2012/04/13/comment-creer-un-robot-sur-twitter ;)',	'Ce tweet a t envoy  partir du robot #AlarmeFatale :)',	'Je peux mentionner @pirmax &amp; @LeBlogdeMaxence dans ce tweet automatique !',);// Mise en variable d'un message alatoire$randomValueArray = array_rand($tweetMsg);$randomMsg = $tweetMsg[$randomValueArray];// Dfinition du message  envoyer, et envoi du message sur Twitterdate_default_timezone_set('GMT');$parameters = array('status' => $randomMsg);$status = $connection->post('statuses/update', $parameters);?>